CREATE SCHEMA SIJARTA;

CREATE TABLE SIJARTA.USER ( 
    Id UUID PRIMARY KEY, 
    Nama VARCHAR(255), 
    JenisKelamin CHAR(1),  
    NoHP VARCHAR(20), 
    Pwd VARCHAR(255), 
    TglLahir DATE, 
    Alamat VARCHAR(255), 
    SaldoMyPay DECIMAL(15, 2) 
); 

CREATE TABLE SIJARTA.PELANGGAN ( 
    Id UUID PRIMARY KEY REFERENCES SIJARTA.USER(Id), 
    Level VARCHAR(50) 
); 

CREATE TABLE SIJARTA.PEKERJA ( 
    Id UUID PRIMARY KEY REFERENCES SIJARTA.USER(Id), 
    NamaBank VARCHAR(100), 
    NomorRekening VARCHAR(50), 
    NPWP VARCHAR(20), 
    LinkFoto VARCHAR(255), 
    Rating FLOAT, 
    JmlPsnananSelesai INT 
); 

CREATE TABLE SIJARTA.METODE_BAYAR ( 
    Id UUID PRIMARY KEY, 
    Nama VARCHAR(100) NOT NULL 
); 

CREATE TABLE SIJARTA.KATEGORI_TR_MYPAY ( 
    Id UUID PRIMARY KEY, 
    Nama VARCHAR(100) 
); 

CREATE TABLE SIJARTA.TR_MYPAY ( 
    Id UUID PRIMARY KEY, 
    UserId UUID REFERENCES SIJARTA.USER(Id), 
    Tgl DATE, 
    Nominal DECIMAL(15, 2), 
    KategoriId UUID REFERENCES SIJARTA.KATEGORI_TR_MYPAY(Id) 
); 

CREATE TABLE SIJARTA.KATEGORI_JASA ( 
    Id UUID PRIMARY KEY, 
    NamaKategori VARCHAR(100) 
); 

CREATE TABLE SIJARTA.PEKERJA_KATEGORI_JASA ( 
    PekerjaId UUID REFERENCES SIJARTA.PEKERJA(Id), 
    KategoriJasaId UUID REFERENCES SIJARTA.KATEGORI_JASA(Id), 
    PRIMARY KEY (PekerjaId, KategoriJasaId) 
); 

CREATE TABLE SIJARTA.SUBKATEGORI_JASA ( 
    Id UUID PRIMARY KEY, 
    NamaSubkategori VARCHAR(100), 
    Deskripsi TEXT, 
    KategoriJasaId UUID REFERENCES SIJARTA.KATEGORI_JASA(Id) 
); 

CREATE TABLE SIJARTA.SESI_LAYANAN ( 
    SubkategoriId UUID REFERENCES SIJARTA.SUBKATEGORI_JASA(Id), 
    Sesi INT, 
    Harga DECIMAL(15, 2), 
    PRIMARY KEY (SubkategoriId, Sesi) 
); 

CREATE TABLE SIJARTA.DISKON ( 
    Kode VARCHAR(50) PRIMARY KEY, 
    Potongan DECIMAL(15, 2) NOT NULL CHECK (Potongan >= 0), 
    MinTrPemesanan INT NOT NULL CHECK (MinTrPemesanan >= 0) 
); 

CREATE TABLE SIJARTA.VOUCHER ( 
    Kode VARCHAR(50) PRIMARY KEY REFERENCES SIJARTA.DISKON(Kode), 
    JmlHariBerlaku INT NOT NULL CHECK (JmlHariBerlaku >= 0), 
    KuotaPenggunaan INT, 
    Harga DECIMAL(15, 2) NOT NULL CHECK (Harga >= 0) 
); 

CREATE TABLE SIJARTA.PROMO ( 
    Kode VARCHAR(50) PRIMARY KEY REFERENCES SIJARTA.DISKON(Kode), 
    TglAkhirBerlaku DATE NOT NULL 
); 

CREATE TABLE SIJARTA.TR_PEMBELIAN_VOUCHER ( 
    Id UUID PRIMARY KEY, 
    TglAwal DATE NOT NULL, 
    TglAkhir DATE NOT NULL, 
    TelahDigunakan INT NOT NULL CHECK (TelahDigunakan >= 0), 
    IdPelanggan UUID REFERENCES SIJARTA.PELANGGAN(Id), 
    IdVoucher VARCHAR(50) REFERENCES SIJARTA.VOUCHER(Kode), 
    IdMetodeBayar UUID REFERENCES SIJARTA.METODE_BAYAR(Id) 
); 

CREATE TABLE SIJARTA.TR_PEMESANAN_JASA (  
    Id UUID PRIMARY KEY,  
    TglPemesanan DATE NOT NULL,  
    TglPekerjaan DATE NOT NULL,  
    WaktuPekerjaan TIMESTAMP NOT NULL,  
    TotalBiaya DECIMAL(15, 2) NOT NULL CHECK (TotalBiaya >= 0),  
    IdPelanggan UUID REFERENCES SIJARTA.PELANGGAN(Id),  
    IdPekerja UUID REFERENCES SIJARTA.PEKERJA(Id),  
    IdKategoriJasa UUID NOT NULL,  -- Part of the composite key 
    Sesi INT NOT NULL,  -- Part of the composite key 
    IdDiskon VARCHAR(50) REFERENCES SIJARTA.DISKON(Kode),  
    IdMetodeBayar UUID REFERENCES SIJARTA.METODE_BAYAR(Id), 
    FOREIGN KEY (IdKategoriJasa, Sesi) REFERENCES SIJARTA.SESI_LAYANAN(SubkategoriId, Sesi) 
); 

CREATE TABLE SIJARTA.STATUS_PESANAN ( 
    Id UUID PRIMARY KEY, 
    Status VARCHAR(50) NOT NULL 
); 

CREATE TABLE SIJARTA.TR_PEMESANAN_STATUS (  
    IdTrPemesanan UUID REFERENCES SIJARTA.TR_PEMESANAN_JASA(Id),  
    IdStatus UUID REFERENCES SIJARTA.STATUS_PESANAN(Id),  
    TglWaktu TIMESTAMP NOT NULL,  -- Use TIMESTAMP instead of DATETIME 
    PRIMARY KEY (IdTrPemesanan, IdStatus)  
); 

CREATE TABLE SIJARTA.TESTIMONI ( 
    IdTrPemesanan UUID REFERENCES SIJARTA.TR_PEMESANAN_JASA(Id), 
    Tgl DATE, 
    Teks TEXT, 
    Rating INT NOT NULL DEFAULT 0, 
    PRIMARY KEY (IdTrPemesanan, Tgl) 
);  



















